import './Registration.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import 'jquery/dist/jquery.min.js'
import 'bootstrap/dist/js/bootstrap.min.js'
import {constants} from "../../utils/constants";

function Registration({setActive}) {

    const handleRegistration = async (e) => {
        e.preventDefault();
    }

    return (
        <div className="body">
            <form onSubmit={handleRegistration}>
                <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Email address</label>
                    <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"
                           placeholder="Enter email"/>
                </div>
                <div className="form-group">
                    <label htmlFor="exampleInputPassword1">Password</label>
                    <input type="password" className="form-control" id="exampleInputPassword1" placeholder="Password"/>
                </div>
                <button type="submit" className="btn btn-primary loginBtn">Login</button>
            </form>
            <div>
                <label className="toLoginLbl" htmlFor="toRegistrationBtn">Already have an account? </label>
                <button id = "toRegistrationBtn" className="btn btn-primary toLoginBtn"
                        onClick={() => {setActive(constants.loginContainer)}}>To login</button>
            </div>
        </div>
    );
}

export default Registration;
