import './Login.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import 'jquery/dist/jquery.min.js'
import 'bootstrap/dist/js/bootstrap.min.js'
import {constants} from "../../utils/constants";
import {useState} from "react";

function Login({setActive}) {

    const [isVisibleErrorField, setIsVisibleErrorField] = useState(false);

    const handleLogin = async (e) => {
        e.preventDefault();
        const data = {
            email: e.target.email.value,
            password: e.target.password.value
        }
        try {
            const response = await fetch('/users/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            });

            if (response.ok) {
                console.log('Type added successfully');
                await fetchTypes();
            } else {
                showError();
                console.error('Failed to login');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    }

    const showError = () => {
        setIsVisibleErrorField(true);
        setTimeout(() => {
            setIsVisibleErrorField(false);
        }, 2000);
    }

    return (
        <div className="body">
            <form onSubmit={handleLogin}>
                <div className="form-group">
                    <label htmlFor="email">Email address</label>
                    <input name="email" type="email" className="form-control" id="email"
                           placeholder="Enter email"/>
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input name="password" type="password" className="form-control" id="password" placeholder="Password"/>
                </div>
                <button type="submit" className="btn btn-primary loginBtn">Login</button>
            </form>
            <div>
                <label className="toRegistrationLbl" htmlFor="toRegistrationBtn">Dont have an account? </label>
                <button id = "toRegistrationBtn" className="btn btn-primary toRegistrationBtn"
                        onClick={() => {setActive(constants.registrationContainer)}}>To registration</button>
            </div>
            {isVisibleErrorField && <p className={"inputError"}>Incorrect input fields!</p>}
        </div>
    );
}

export default Login;
