function CustomNotification({ body }) {
    // setShow(true);
    // setTimeout(() => {
    //     setShow(false);
    // }, 2000);
    return (
        <div style={{ border: '1px solid #ccc', padding: '10px', backgroundColor: '#000', position: 'fixed', right: '10px', top: '10px', zIndex: 1000, borderColor: "white" }}>
            <h3>Notification</h3>
            <div dangerouslySetInnerHTML={{ __html: body }} />
        </div>
    );
}

export default CustomNotification;
