import {useEffect} from "react";

const NotificationComponent = () => {
    useEffect(() => {
        if (Notification.permission !== 'granted') {
            Notification.requestPermission();
        }
    }, []);

    const showNotification = () => {
        if (Notification.permission === 'granted') {
            new Notification('Hello!', {
                body: 'This is a notification from your React app.',
                icon: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQPqyE5XaLElmyLAjPYhPEMcn6gMlg2vVPIeA&s', // Замените на свой URL для иконки
            });
        } else {
            console.log('Notification permission not granted');
        }
    };

    return (
        <div>
            <h1>Уведомления в React</h1>
            <button onClick={showNotification}>Показать уведомление</button>
        </div>
    );
};

export default NotificationComponent;
